class Ajax {
  constructor () {
    this.data_ = {};
    this.url_ = "/restaurante/php/delete.php";    
    this.where_
  }
  url (str) {    
    this.url_ =str;
    return this // Aqui!
  }
  data (obj) {
    this.data_ = obj;    
    return this // Aqui!
  }
  where(w){
    this.where_ = w;
    return this;
  }
  post(call){    
    this.execute('post',function(r){
      console.log('post',r);
    call(r);  
  });
    
  }
  get(){    
    this.execute('get');
  } 
  execute(method,call){

    $.ajax({
      url : this.url_,
      type : 'post',
      data : this.data_,
      beforeSend : function(){
        console.log('before');
      }
    })
    .done(function(msg){      
      call(msg);
    })
    .fail(function(jqXHR, textStatus, msg){
      alert(msg);
    }); 
  }
}